function TimelineChart() {
  const dates = [
    "Aug 16",
    "Aug 17",
    "Aug 18",
    "Aug 19",
    "Aug 20",
    "Aug 21",
    "Aug 22",
  ];
  return (
    <div className="chartSection">
      <div className="chartLine">
        {dates.map((date, idx) => (
          <div
            key={date}
            className="chartDot"
            style={{ left: `${(idx / (dates.length - 1)) * 99}%` }}
          />
        ))}
      </div>
      <div className="chartLabels">
        {dates.map((date) => (
          <span key={date}>{date}</span>
        ))}
      </div>
    </div>
  );
}

